mapper.select:
SELECT PASS 
FROM LOGIN 
WHERE ID= #{id}

mapper.update:
UPDATE BBS
SET POS = POS + 1
WHERE groupId=#{groupId} and pos>#{pos}

mapper.select:
SELECT articleNum, id, REGEXP_REPLACE(title, '<[^>]*>|\&([^;])*;','')title, content, depth, hit, groupid, pos, writedate, filestatus
FROM BBS 
WHERE articleNum=#{articleNum}

SELECT FILENUM, ORIGINFNAME, STOREDFNAME
FROM FILELIST
WHERE ARTICLENUM = #{aritcleNum}

SELECT TITLE,CONTENT 
FROM BBS 
WHERE ARTICLENUM=#{articleNum}

mapper.delete:
DELETE FROM FILELIST
WHERE FILENUM IN
#{fileNum}

mapper.update:
UPDATE BBS
SET TITLE = #{title}, CONTENT = #{content}, FILESTATUS = #{fileStatus}
WHERE ARTICLENUM = #{articleNum}

mapper.select:
SELECT STOREDFNAME
FROM FILELIST
WHERE FILENUM=#{fileNum}

select count(*)
from fileList
where articleNum=#{articleNum}

SELECT COUNT(*)
FROM COMMENTS
WHERE ARTICLENUM = #{articleNum}
